examples.example:
{
  forumBySlug(slug: "slug-life") {
    nodeId
    id
    slug
    name
    description
  }
}

{
  allForums(first: 1, offset: 1) {
    nodes {
      nodeId
      id
      name
    }
  }
}

{
  forumBySlug(slug: "cat-life") {
    nodeId
    id
    name
    topics(
      condition: { authorId: 1 }
    ) {
      nodes {
        nodeId
        id
        title
      }
    }
  }
}

{
  forumById(id: 1) {
    name
    topics(
      first: 1
      orderBy: [CREATED_AT_ASC]
    ) {
      nodes {
        id
        title
        bodySummary
        author {
          id
          username
        }
        posts(
          first: 1
          orderBy: [ID_DESC]
        ) {
          nodes {
            id
            author {
              id
              username
            }
            body
          }
        }
      }
    }
  }
}

