examples.example:
mutation {
  updateTopicById(
    input: {
      id: 1
      topicPatch: {
        title: "My (edited) title"
      }
    }
  ) {
    topic {
      nodeId
      id
      title
      body
    }
  }
}

# Works for a table like:
#
#   create table app_public.topics (
#     id serial primary key,
#     forum_id integer NOT NULL references app_public.forums on delete cascade,
#     title text NOT NULL,
#     body text DEFAULT ''::text NOT NULL
#   );

