examples.query:
{
  forumBySlug(slug: "slug-life") {
    nodeId
    id
    slug
    name
    description
    createdAt
    updatedAt
  }
}

{
  allForums {
    nodes {
      nodeId
      id
      slug
      name
      description
      createdAt
      updatedAt
    }
  }
}

{
  allForums(first: 1, offset: 1) {
    nodes {
      nodeId
      id
      name
    }
  }
}

{
  forumBySlug(slug: "cat-life") {
    nodeId
    id
    name
    topics(condition: { userId: 1 }) {
      nodes {
        nodeId
        id
        title
      }
    }
  }
}
