examples.query:
{
  allForums {
    nodes {
      nodeId
      id
      slug
      name
      description
      createdAt
      updatedAt
    }
  }
}

{
  forumBySlug(slug: "slug-life") {
    nodeId
    id
    slug
    name
    description
    createdAt
    updatedAt
  }
}

{
  allForums(first: 1, offset: 1) {
    nodes {
      nodeId
      id
      name
    }
  }
}

{
  forumBySlug(slug: "cat-life") {
    nodeId
    id
    name
    topics(condition: { userId: 1 }) {
      nodes {
        nodeId
        id
        title
      }
    }
  }
}

mutation {
  createTopic(
    input: {
      topic: {
        forumId: 1
        title: "My question relates to mutations..."
        body: "How do you write them?"
      }
    }
  ) {
    topic {
      nodeId
      id
      forumId
      title
      body
    }
  }
}

examples.result:
{
  "createTopic": {
    "topic": {
      "nodeId": "WyJ0b3BpY3MiLDRd",
      "id": 4,
      "forumId": 1,
      "title":
        "My question relates to mutations...",
      "body": "How do you write them?"
    }
  }
}

examples.query:
mutation {
  updateTopicById(
    input: {
      id: 1
      topicPatch: { title: "My (edited) title" }
    }
  ) {
    topic {
      nodeId
      id
      title
      body
      userId
    }
  }
}

examples.result:
{
  "updateTopicById": {
    "topic": {
      "nodeId": "WyJ0b3BpY3MiLDFd",
      "id": 1,
      "title": "My (edited) title",
      "body":
        "lets discuss cats because theyre totally cool",
      "userId": 1
    }
  }
}

