examples.example:
/**
 * Simply renames the `UserPatch` and `PostPatch` type names to be called
 * `UserPatchSet` and `PostPatchSet` instead.
 *
 * Not particularly useful, just an example. ('PatchSet' chosen to minimise
 * diff to make example clearer.)
 *
 * Replaces this inflector:
 * https://github.com/graphile/graphile-engine/blob/f3fb3878692c6959e481e517375da66503428dc5/packages/graphile-build-pg/src/plugins/PgBasicsPlugin.js#L309-L311
 */
module.exports = function PgRenamePatchToPatchSetPlugin(
  builder
) {
  builder.hook(
    "inflection",
    inflector => ({
      // Retain the existing inflectors
      ...inflector,

      // Override the patchType inflector
      patchType(typeName) {
        // return this.upperCamelCase(`${typeName}-patch`);
        return this.upperCamelCase(
          `${typeName}-patch-set`
        );
      },
    })
  );
};

/**
 * Simply renames the `allUsers` and `allPosts` Query fields to `users` and
 * `posts` respectively.
 *
 * Not particularly useful, just an example.
 *
 * Replaces this inflector:
 * https://github.com/graphile/graphile-engine/blob/f3fb3878692c6959e481e517375da66503428dc5/packages/graphile-build-pg/src/plugins/PgBasicsPlugin.js#L460-L464
 */
module.exports = function PgShortenAllRowsInflectorPlugin(
  builder
) {
  builder.hook(
    "inflection",
    inflector => ({
      // Retain the existing inflectors
      ...inflector,

      // Override the allRows inflector
      allRows(table) {
        return this.camelCase(
          // Was: `all-${this.pluralize(this._singularizedTableName(table))}`
          // Now:
          this.pluralize(
            this._singularizedTableName(
              table
            )
          )
        );
      },
    })
  );
};

/**
 * Use of this plugin is NOT recommended, please see
 * PgSmallNumericToFloatPlugin for a more appropriate replacement if you need
 * one.
 *
 * This plugin will have PostGraphile use `GraphQLFloat` instead of `BigFloat`
 * for *all* DECIMAL / NUMERIC values, for making PostGraphile v4 slightly more
 * backwards-compatible with v3.
 *
 * It's generally a bad idea to use floating point numbers to represent
 * arbitrary precision numbers such as NUMERIC because loss of precision can
 * occur.
 */
module.exports = function PgNumericToFloatPlugin(
  builder
) {
  builder.hook("build", build => {
    // Register a type handler for NUMERIC / DECIMAL (oid = 1700), always
    // returning the GraphQLFloat type
    build.pgRegisterGqlTypeByTypeId(
      "1700",
      () => build.graphql.GraphQLFloat
    );
    return build;
  });
};

/**
 * This plugin will have PostGraphile use `GraphQLFloat` instead of `BigFloat`
 * for DECIMAL / NUMERIC values that have a precision and scale under the given
 * limits (currently 12 and 2 respectively).
 *
 * It's generally a bad idea to use floating point numbers to represent
 * arbitrary precision numbers such as NUMERIC because loss of precision can
 * occur; however some systems are okay with this compromise.
 */
module.exports = function PgSmallNumericToFloatPlugin(
  builder,
  {
    pgNumericToFloatPrecisionCap = 12,
    pgNumericToFloatScaleCap = 2,
  }
) {
  builder.hook("build", build => {
    // Register a type handler for NUMERIC / DECIMAL (oid = 1700)
    build.pgRegisterGqlTypeByTypeId(
      "1700",
      (_set, modifier) => {
        if (
          modifier &&
          typeof modifier ===
            "number" &&
          modifier > 0
        ) {
          // Ref: https://stackoverflow.com/a/3351120/141284
          const precision =
            ((modifier - 4) >> 16) &
            65535;
          const scale =
            (modifier - 4) & 65535;
          if (
            precision <=
              pgNumericToFloatPrecisionCap &&
            scale <=
              pgNumericToFloatScaleCap
          ) {
            // This number is no more precise than our cap, so we're declaring
            // that we can handle it as a float:
            return build.graphql
              .GraphQLFloat;
          }
        }
        // If all else fails, let PostGraphile do it's default handling - i.e.
        // BigFloat
        return null;
      }
    );

    // We didn't modify _init, but we still must return it.
    return build;
  });
};

/**
 * This plugin sets a defaultValue on all input object fields that match the
 * given criteria (specifically the 'create' input types, for columns named
 * 'name')
 */
module.exports = function SetInputObjectDefaultValue(
  builder
) {
  builder.hook(
    "GraphQLInputObjectType:fields:field",
    (field, build, context) => {
      const {
        scope: {
          isPgRowType,
          isInputType,
          isPgPatch,
          pgFieldIntrospection: attr,
        },
      } = context;
      if (
        !isPgRowType ||
        !isInputType ||
        isPgPatch ||
        !attr ||
        attr.kind !== "attribute" ||
        attr.name !== "name"
      ) {
        return field;
      }

      return {
        ...field,
        type: build.graphql.getNamedType(
          field.type
        ), // Since it has a default, it can be nullable
        defaultValue:
          // attr.tags.defaultValue: enables overriding this with a
          // `@defaultValue Alice Smith` smart comment
          attr.tags.defaultValue ||
          "Bobby Tables",
      };
    }
  );
};

/**
 * This plugin sets the `input.quizPatch.updatedAt` to the current timestamp in
 * the `updateQuiz*` mutations IFF it's not already set.
 */
module.exports = function SetInputObjectDefaultValue(
  builder
) {
  builder.hook(
    "GraphQLObjectType:fields:field",
    (field, build, context) => {
      const {
        scope: {
          isPgUpdateMutationField,
          pgFieldIntrospection: table,
        },
      } = context;
      if (
        !isPgUpdateMutationField ||
        table.kind !== "class" ||
        table.name !== "quiz"
      ) {
        return field;
      }

      const oldResolve =
        field.resolve;

      return {
        ...field,
        resolve(
          _mutation,
          args,
          context,
          info
        ) {
          // Override the `updatedAt` field if it's not already set.
          if (
            args.input.quizPatch
              .updatedAt == null
          ) {
            args.input.quizPatch.updatedAt = new Date().toISOString();
          }
          return oldResolve(
            _mutation,
            args,
            context,
            info
          );
        },
      };
    }
  );
};

// Tested via:
// npx postgraphile --append-plugins @graphile-contrib/pg-simplify-inflector,`pwd`/examples/plugins/0300_mutation_wrappers/OverrideArgValuePlugin.js -c graphile_org_demo -s app_public

/**
 * This plugin treats any table that doesn't have an `@omit` comment as if it
 * had `@omit create,update,delete` (thereby disabling mutations).
 *
 * Override it by adding a smart comment to the table. To restore all
 * mutations, do `COMMENT ON my_table IS E'@omit :';` (the `:` is special
 * syntax for "nothing").
 */
module.exports = function OmitMutationsByDefaultPlugin(
  builder
) {
  builder.hook("build", build => {
    const {
      pgIntrospectionResultsByKind,
    } = build;
    pgIntrospectionResultsByKind.class
      .filter(
        table =>
          table.isSelectable &&
          table.namespace
      )
      .forEach(table => {
        if (!("omit" in table.tags)) {
          table.tags.omit =
            "create,update,delete";
        }
      });
    return build;
  });
};

// Tested via:
